mapper.select:
SELECT C.CLASS_TYPE, C.SEQ, M.MEMBER_NAME AS CLASS_CREATOR_NAME, C.CLASS_NAME, C.CLASS_INFO, C.CLASS_AREA,
REPLACE(TO_CHAR(c.class_starttime, '00,00'),',',':') as class_starttime
FROM CLASS C, MEMBER M, (SELECT CLASS.SEQ, ROUND(AVG(REVIEW.REVIEW_RATING),1) AS CLASS_RATING,COUNT(REVIEW_CLASS_SEQ) AS CLASS_REVIEW_COUNT FROM REVIEW, CLASS WHERE REVIEW.REVIEW_CLASS_SEQ = CLASS.SEQ GROUP BY CLASS.SEQ ORDER BY CLASS_RATING DESC) R, (SELECT CLASS.SEQ,(TO_CHAR(CLASS.CLASS_SD,'YYYYMMDD')-TO_CHAR(SYSDATE,'YYYYMMDD')) AS CLASS_D_DAY FROM CLASS) D_DAY, (SELECT CL.SEQ,COUNT(INC.INCLASS_CLASS_SEQ) AS CLASS_NOW_PARTICIPATION FROM INCLASS INC, CLASS CL WHERE INC.INCLASS_CLASS_SEQ = CL.SEQ GROUP BY CL.SEQ) PARTI WHERE C.SEQ = R.SEQ (+) AND C.CLASS_MEMBER_SEQ = M.MEMBER_SEQ(+) AND C.SEQ = D_DAY.SEQ AND C.SEQ = PARTI.SEQ (+) and c.CLASS_TERMIN = #{class_termin}

mapper.update:
UPDATE class SET class_depa = #{class_depa},class_name = #{class_name},
class_info = #{class_info},class_area = #{class_area},
class_detail = #{class_detail},class_img = #{class_img} 
WHERE seq = #{seq}
